window.emoticon = (function () {
    var pub = {}, //publica
            // privadas a partir de acá            
            urlImagenes = "img/",
            expresionRegular = //,
            emoticones = { // ver como ensamblar esto, quizas de base de datos o de un archivo de texto. Será mantenible????
                ":)": "alegre.gif",
                "rofl": "rofl.gif",
                ":p": "lengua.gif"
            };
    //pub.ingredient = 'Bacon Strips'; //agregamos propiedades publicaspublico

    //Public method
    pub.agregarEmoticones = function (mensaje) {        
        return mensaje.replace(expresionRegular, function (cadenaEncontrada, indice, cadena) {            
            return "<span class='emoticon'><img src='" + urlImagenes + emoticones[cadenaEncontrada.toLowerCase()]+ "'></span>";
        });
    };

    (function () {        
        var todosLosEmoticones = [],
                emoticon;
        for (emoticon in emoticones) {
            todosLosEmoticones.push(emoticon.replace(/[.^$*+?()[{\\|\]-]/g, '\\$&'));            
        }
        expresionRegular = new RegExp(todosLosEmoticones.join('|'), "gi");
    })();

    //Return just the public parts
    return pub;
}());