window.emoticon = (function () {
    var pub = {}, //publica
            // privadas a partir de ac√°            
            urlImagenes = "img/",
            expresionRegular = //,
            caritas = { // ver como ensamblar esto, quizas de base de datos o de un archivo de texto, aunque no sera mantenible????
                ":)": ["alegre.gif"],
                "rofl": ["rofl.gif"]
            };
    //pub.ingredient = 'Bacon Strips'; //agregamos propiedades publicaspublico

    //Public method
    pub.agregarEmoticones = function (mensaje) {        
        return mensaje.replace(expresionRegular, function (cadenaEncontrada, indice, cadena) {            
            return "<span class='emoticon'><img src='" + urlImagenes + caritas[cadenaEncontrada]+ "'></span>";
        });
    };

    (function creaExpresionRegularParaEmoticones() {        
        var todosLosEmoticones = [],
                carita;
        for (carita in caritas) {
            todosLosEmoticones.push(carita.replace(/[.^$*+?()[{\\|\]-]/g, '\\$&'));            
        }
        expresionRegular = new RegExp(todosLosEmoticones.join('|'), "g");
    })();

    //Return just the public parts
    return pub;
}());